mapper.update:
UPDATE Employee
SET user_id = #{user_id}, employee_name = #{employee_name}, birth = #{birth}, role = #{role}, image_name = #{image_name},
	real_image = #{real_image}, employee_email = #{employee_email}
WHERE employ_id = #{employ_id}

UPDATE Employee
SET pwd = #{pwd}
WHERE employ_id = #{employ_id}
